CREATE TABLE "public"."decision" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "orgId" uuid NOT NULL, "circleId" uuid NOT NULL, "memberId" uuid NOT NULL, "title" Text NOT NULL DEFAULT ''::text, "description" Text NOT NULL DEFAULT ''::text, "archived" bool NOT NULL DEFAULT false, "createdAT" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("orgId") REFERENCES "public"."org"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("circleId") REFERENCES "public"."circle"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("memberId") REFERENCES "public"."member"("id") ON UPDATE restrict ON DELETE restrict);
CREATE EXTENSION IF NOT EXISTS pgcrypto;
