CREATE TABLE "public"."thread_activity" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "orgId" uuid NOT NULL, "threadId" uuid NOT NULL, "userId" uuid NOT NULL, "createdAt" timestamptz NOT NULL DEFAULT now(), "type" text NOT NULL, "data" json NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("orgId") REFERENCES "public"."org"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("threadId") REFERENCES "public"."thread"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("userId") REFERENCES "auth"."users"("id") ON UPDATE restrict ON DELETE restrict);
CREATE EXTENSION IF NOT EXISTS pgcrypto;
