CREATE TABLE "public"."task" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "orgId" uuid NOT NULL, "circleId" uuid NOT NULL, "memberId" uuid, "title" text NOT NULL DEFAULT '''::text', "description" text NOT NULL DEFAULT '''::text', "archived" boolean NOT NULL DEFAULT false, "createdAt" timestamptz NOT NULL DEFAULT now(), "dueDate" timestamptz, "status" text NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("orgId") REFERENCES "public"."org"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("circleId") REFERENCES "public"."circle"("id") ON UPDATE cascade ON DELETE restrict, FOREIGN KEY ("memberId") REFERENCES "public"."member"("id") ON UPDATE restrict ON DELETE set null);
CREATE EXTENSION IF NOT EXISTS pgcrypto;
