CREATE TABLE "public"."thread" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "orgId" uuid NOT NULL, "circleId" uuid NOT NULL, "participantsScope" text NOT NULL, "participantsMembersIds" json NOT NULL, "initiatorMemberId" uuid NOT NULL, "title" text NOT NULL, "createdAt" timestamptz NOT NULL DEFAULT now(), "archived" bool NOT NULL DEFAULT false, "lastActivityId" uuid, "lastActivityDate" timestamptz, PRIMARY KEY ("id") , FOREIGN KEY ("orgId") REFERENCES "public"."org"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("circleId") REFERENCES "public"."circle"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("initiatorMemberId") REFERENCES "public"."member"("id") ON UPDATE restrict ON DELETE cascade);
CREATE EXTENSION IF NOT EXISTS pgcrypto;
