CREATE TABLE "public"."meeting" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "orgId" uuid NOT NULL, "initiatorMemberId" uuid NOT NULL, "facilitatorMemberId" uuid NOT NULL, "createdAt" timestamptz NOT NULL DEFAULT now(), "startDate" timestamptz NOT NULL, "endDate" timestamptz NOT NULL, "ended" bool NOT NULL DEFAULT false, "title" text NOT NULL, "attendees" json, "stepsConfig" json NOT NULL, "currentStepId" uuid, "archived" bool NOT NULL DEFAULT false, "videoConf" text, PRIMARY KEY ("id") , FOREIGN KEY ("orgId") REFERENCES "public"."org"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("initiatorMemberId") REFERENCES "public"."member"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("facilitatorMemberId") REFERENCES "public"."member"("id") ON UPDATE restrict ON DELETE cascade);
CREATE EXTENSION IF NOT EXISTS pgcrypto;
